    const eventData={

        title:data.get('title'),
        image:data.get('image'),
        date:data.get('date'),
        description:data.get('description'),
    }

   const respose=fetch("http://localhost:8080/events", { method: "POST",headers:{'Content-Type':'application/json'} ,body:JSON.stringify(eventData) });
}
